import React, { useState } from "react";
import Investor from "../Entity/Investor";
// import { Container, Dropdown, DropdownButton, DropdownItem } from "react-bootstrap";
import Dropdown from "react-bootstrap/Dropdown";
import DropdownButton from "react-bootstrap/DropdownButton";
import DropdownItem from "react-bootstrap/DropdownButton";
import Commitments from "./Commitments";


type InvestorDetailsProps = {
    investor: Investor
}

function InvestorDetails(props: InvestorDetailsProps) {
    const assetClasses = ["pe", "pd", "re", "inf", "nr", "hf"];
    // const [investorID, setInvestorID] = useState(props.investor.firm_id);
    const [assetClass, setAssetClass] = useState("");
    const [assetSelected, setAssetSelected] = useState(false);

    function selectAssetClass(asset: string) {
        setAssetClass(asset);
        setAssetSelected(true);
    }

    return (
        <div className="InvestorDetails">
            <h2>{props.investor.firm_name} Details</h2>
            <DropdownButton id="asset-selector" title="Asset Type" menuVariant="dark">
                <Dropdown.Item onSelect={() => selectAssetClass(assetClasses[0])} >
                    Private Equity
                </Dropdown.Item>
                <Dropdown.Item onSelect={() => selectAssetClass(assetClasses[1])}>
                    Private Debt
                </Dropdown.Item>
                <Dropdown.Item onSelect={() => selectAssetClass(assetClasses[2])}>
                    Real Estate
                </Dropdown.Item>
                <Dropdown.Item onSelect={() => selectAssetClass(assetClasses[3])}>
                    Infrastructure
                </Dropdown.Item>
                <Dropdown.Item onSelect={() => selectAssetClass(assetClasses[4])}>
                    Natural Resources
                </Dropdown.Item>
                <Dropdown.Item onSelect={() => selectAssetClass(assetClasses[5])}>
                    Hedge Funds
                </Dropdown.Item>
            </DropdownButton>


            {assetSelected && (
                <Commitments investor_id={props.investor.firm_id} asset_class={assetClass}/>
            )
            }


        </div>
    );
}

export default InvestorDetails;
